mapper.select:
select lsh, item_id,ddsqsj,ywshsj,yjdhrq, cgsl, cgdj, cgzje, zt
from tb_cg
where lsh = #{lsh,jdbcType=VARCHAR}

SELECT b.lsh, b.item_id, a.item_name, (select dmmc from tb_dic_zddm where dmlb="SPLB" and dmz=a.lb) lb, b.ddsqsj, b.ywshsj, b.yjdhrq, b.cgsl, b.cgdj, b.cgzje, case when b.zt ='00' then '新增' when b.zt ='01' then '审核通过' when b.zt ='02' then '审核未通过' else '撤销' end zt FROM tb_item a,tb_cg b WHERE a.item_id = b.item_id
and b.lsh = #{lsh,jdbcType=VARCHAR}
and a.item_id = #{item_id,jdbcType=VARCHAR}
and b.item_name like concat('%',concat(#{item_name,jdbcType=VARCHAR},'%'))
and a.lb = #{lb,jdbcType=VARCHAR}
and date_format(b.ddsqsj,'%Y-%m-%d') between #{sqqssj,jdbcType=VARCHAR} and #{sqjssj,jdbcType=VARCHAR} LIMIT #{start} ,#{end}

SELECT count(1)
FROM tb_item a,tb_cg b
WHERE a.item_id = b.item_id

  and b.lsh = #{lsh,jdbcType=VARCHAR}


  and a.item_id = #{item_id,jdbcType=VARCHAR}


  and b.item_name like concat('%',concat(#{item_name,jdbcType=VARCHAR},'%'))


  and a.lb = #{lb,jdbcType=VARCHAR}

and date_format(b.ddsqsj,'%Y-%m-%d') between #{sqqssj,jdbcType=VARCHAR} and #{sqjssj,jdbcType=VARCHAR}
