mapper.delete:
delete from tb_dd
where ddh = #{ddh,jdbcType=VARCHAR}
  and ddzt ='00'

mapper.update:
update tb_dd
set psfs = #{psfs,jdbcType=VARCHAR}
where ddh = #{ddh,jdbcType=VARCHAR}
  and ddzt ='01' or ddzt ='00'

mapper.select:
select ddh, jyrq, ddzt, psfs,dgkh,ddzje
from tb_dd
where ddh = #{ddh,jdbcType=VARCHAR}

SELECT b.ddh, (select user_name from tb_user where user_id=b.dgkh) dgkh, b.jyrq, b.ddzje, case when b.ddzt ='00' then '新增' when b.ddzt ='01' then '下单' when b.ddzt ='02' then '发货' when b.ddzt ='03' then '退货' when b.ddzt ='04' then '撤销' when b.ddzt ='05' then '确认收货' else 'error' end ddzt, case when b.psfs ='1' then '配送中心配送' when b.psfs ='2' then '仓库配送' when b.psfs ='3' then '商店配送' else null end psfs FROM tb_dd b,tb_dd_mx a WHERE a.ddh = b.ddh
and b.ddh = #{lsh,jdbcType=VARCHAR}
and a.item_id = #{item_id,jdbcType=VARCHAR}
and a.item_id in (select item_id from tb_item where item_name like concat('%',concat(#{item_name,jdbcType=VARCHAR},'%')))
and date_format(b.jyrq,'%Y-%m-%d') between #{jyqssj,jdbcType=VARCHAR} and #{jyjssj,jdbcType=VARCHAR} LIMIT #{start} ,#{end}

SELECT count(1)
FROM
tb_dd b,tb_dd_mx a
WHERE
a.ddh = b.ddh

  and b.ddh = #{lsh,jdbcType=VARCHAR}


  and a.item_id = #{item_id,jdbcType=VARCHAR}


  and a.item_id in (select item_id from tb_item where item_name like concat('%',concat(#{item_name,jdbcType=VARCHAR},'%')))

and date_format(b.jyrq,'%Y-%m-%d') between #{jyqssj,jdbcType=VARCHAR} and #{jyjssj,jdbcType=VARCHAR}

mapper.update:
update tb_dd
set ddzt=#{ddzt,jdbcType=VARCHAR}
where ddh = #{ddh,jdbcType=VARCHAR}

mapper.select:
SELECT a.ddh, a.mxlsh, a.item_id, c.item_name, c.lb, a.sl, a.dxzje FROM tb_dd b,tb_dd_mx a,tb_item c WHERE a.ddh = b.ddh and b.ddh=#{ddh,jdbcType=VARCHAR} and a.item_id = c.item_id
and a.mxlsh = #{mxlsh,jdbcType=VARCHAR}
and a.item_id = #{item_id,jdbcType=VARCHAR}
and c.item_name like concat('%',concat(#{item_name,jdbcType=VARCHAR},'%'))
LIMIT #{start} ,#{end}

select count(1) FROM
tb_dd b,tb_dd_mx a
WHERE
a.ddh = b.ddh
and b.ddh=#{ddh,jdbcType=VARCHAR}

  and a.mxlsh = #{mxlsh,jdbcType=VARCHAR}


  and a.item_id = #{item_id,jdbcType=VARCHAR}


  and a.item_id in (select item_id from tb_item where item_name like concat('%',concat(#{item_name,jdbcType=VARCHAR},'%')))

mapper.update:
update tb_dd_mx
set item_id = #{item_id,jdbcType=VARCHAR},
  sl = #{sl,jdbcType=INTEGER},
  dxzje =#{dxzje,jdbcType=DOUBLE}
where mxlsh = #{mxlsh,jdbcType=VARCHAR}
