mapper.select:
SELECT b.sklsh, b.sksj, b.ddh, b.ddzje, b.skzje, b.user_id, (select user_name from tb_user where user_id=b.user_id) user_name, (select user_name from tb_user where user_id=b.skr) skr, case when b.skzt ='2' then '已收款' else '待审核' end skzt, case when b.skfs ='1' then '银行转账' when b.skfs ='2' then '支付宝收款' else '微信收款' end skfs FROM tb_jsjl_sk b WHERE 1=1
and b.sklsh = #{sklsh,jdbcType=VARCHAR}
and b.ddh = #{ddh,jdbcType=VARCHAR}
and b.user_id in (select user_id from tb_user where user_name like concat('%',concat(#{user_name,jdbcType=VARCHAR},'%')))
and b.skzt = #{skfs,jdbcType=VARCHAR}
and date_format(b.sksj,'%Y-%m-%d') between #{skqssj,jdbcType=VARCHAR} and #{skjssj,jdbcType=VARCHAR} LIMIT #{start} ,#{end}

SELECT count(1)  FROM
tb_jsjl_sk b
WHERE 1=1

  and b.sklsh = #{sklsh,jdbcType=VARCHAR}


  and b.ddh = #{ddh,jdbcType=VARCHAR}


  and b.user_id in (select user_id from tb_user where user_name like concat('%',concat(#{user_name,jdbcType=VARCHAR},'%')))


  and b.skzt = #{skfs,jdbcType=VARCHAR}

and date_format(b.sksj,'%Y-%m-%d') between #{skqssj,jdbcType=VARCHAR} and #{skjssj,jdbcType=VARCHAR}

SELECT b.jylsh, b.ddh, b.user_id, (select user_name from tb_user where user_id=b.user_id) user_name, b.jyrq, b.bz, (select user_name from tb_user where user_id=b.czr) czr, b.zfje, case when b.skfs ='1' then '银行转账' when b.skfs ='2' then '支付宝收款' else '微信收款' end skfs FROM tb_jsjl b WHERE 1=1
and b.jylsh = #{jylsh,jdbcType=VARCHAR}
and b.ddh = #{ddh,jdbcType=VARCHAR}
and b.user_id in (select user_id from tb_user where user_name like concat('%',concat(#{user_name,jdbcType=VARCHAR},'%')))
and b.skfs = #{skfs,jdbcType=VARCHAR}
and date_format(b.jyrq,'%Y-%m-%d') between #{jyqssj,jdbcType=VARCHAR} and #{jyjssj,jdbcType=VARCHAR} LIMIT #{start} ,#{end}

SELECT count(1) FROM   tb_jsjl b
WHERE 1=1

  and b.jylsh = #{jylsh,jdbcType=VARCHAR}


  and b.ddh = #{ddh,jdbcType=VARCHAR}


  and b.user_id in (select user_id from tb_user where user_name like concat('%',concat(#{user_name,jdbcType=VARCHAR},'%')))


  and b.skfs = #{skfs,jdbcType=VARCHAR}

and date_format(b.jyrq,'%Y-%m-%d') between #{jyqssj,jdbcType=VARCHAR} and #{jyjssj,jdbcType=VARCHAR}
